package com.mhy.lefinder.util;

public class LEFException extends Exception {

	/**
	 * auto generated
	 */
	private static final long serialVersionUID = 4268613121919391975L;

	public LEFException(String msg){
		super(msg);
	}
	
	public LEFException(Throwable thr){
		super(thr);
	}
	
	public LEFException(String msg, Throwable thr){
		super(msg, thr);
	}
	
}
